import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";

// These functions are defined outside of the component to insure they do not have access to state
// and are, therefore more likely to be pure.

/**
 * Update the session state with new state after each tick of the interval.
 * @param prevState
 *  the previous session state
 * @returns
 *  new session state with timing information updated.
 */
function nextTick(prevState) {
  const focus = prevState.session.label;
  const timeRemaining = Math.max(0, prevState.session.timeRemaining - 1);
  return {
    ...prevState,
    session: {
      label: focus,
      timeRemaining: timeRemaining}
  };
}


/**
 * Higher order function that returns a function to update the session state with the next session type upon timeout.
 * @param focusDuration
 *    the current focus duration
 * @param breakDuration
 *    the current break duration
 * @returns
 *  function to update the session state.
 */
function nextSession(focusDuration, breakDuration) {
  /**
   * State function to transition the current session type to the next session. e.g. On Break -> Focusing or Focusing -> On Break
   */
  return (currentSession) => {
    if (currentSession.session.label === "Focusing") {
      return {
        ...currentSession,
        session: {
        label: "On Break",
        timeRemaining: breakDuration * 60,
        }
      };
    }
    return {
      ...currentSession,
      session: {
      label: "Focusing",
      timeRemaining: focusDuration * 60,
      }
    };
  };
}

function Pomodoro() {
  const initialFormState = {
    isTimerRunning: false,
    session: null,
    disable: false,
    focusDuration: 25,
  }
  const[formData, setFormData] = useState({...initialFormState});
  // Timer starts out paused
  // const [isTimerRunning, setIsTimerRunning] = useState(false);
  // The current session - null where there is no session running
  // const [session, setSession] = useState(null);

  // const [disable, setDisable] = useState(false);

  // ToDo: Allow the user to adjust the focus and break duration.
  let focusDuration = 25;
  const breakDuration = 5;

  const focusDurationHandler = (event) => {
    console.log(event.target.value)
    // let newValue = focusDuration;
    let focus = formData.focusDuration
    if (event.target.value === 'decrease') {
      focus -= 1;
      setFormData({...formData, focusDuration: focus})
    }
    else {
      focus += 1;
      setFormData({...formData, focusDuration: focus})}
    // console.log(event.target.value)
    // event.target.value === 'decrease' ? newValue -= 1 : newValue += 1
    // console.log(formData.focusDuration);
  }

  /**
   * Custom hook that invokes the callback function every second
   *
   * NOTE: You will not need to make changes to the callback function
   */
  useInterval(() => { 
      if (formData.session.timeRemaining === 0) {
        new Audio("https://bigsoundbank.com/UPLOAD/mp3/1482.mp3").play();
        return setFormData(nextSession(focusDuration, breakDuration));
      }
      return setFormData(nextTick);
    },
    formData.isTimerRunning ? 1000 : null
  );

  /**
   * Called whenever the play/pause button is clicked.
   */
  function playPause() {
    setFormData((prevState) => {
      // setting state to opposite of prevState
      const nextState = !prevState.isTimerRunning;
      // if now playing ('true')
      if (nextState) {
        // setSession((prevStateSession) => {
          ///////////
          ///////////
          if(prevState.session === null) {
            return {
              ...formData,
              isTimerRunning: nextState,
              session: {
              label: "Focusing",
              timeRemaining: focusDuration * 60,
            }}
          }
        // setFormData((prevStateSession) => {
        //   // If the timer is starting and the previous session is null,
        //   // start a focusing session.
        //   if (prevStateSession.session === null) {
        //     return {
        //       label: "Focusing",
        //       timeRemaining: focusDuration * 60,
        //     };
        //   }
        //   // continue the session
        return {...formData,
                isTimerRunning: nextState,
                session: prevState.session}
        //   return prevStateSession.session;
        // });
      }
      ///////////
      ///////////
      // if now paused ('false')
      if(!nextState) {return {...formData, isTimerRunning: nextState}};
    });
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: 25:00
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={focusDurationHandler}
                value="decrease"
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: 05:00
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
              // onClick={() => setDisable(true)}
              // disabled={disable}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !formData.isTimerRunning,
                  "oi-media-pause": formData.isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session. and disable the stop button when there is no active session */}
            {/* TODO: Disable the stop button when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              data-testid="stop"
              title="Stop the session"
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
        <div className="row mb-2">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
            <h2 data-testid="session-title">
              {formData.session?.label} for 25:00 minutes
            </h2>
            {/* TODO: Update message below correctly format the time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {formData.session?.timeRemaining} remaining
            </p>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
