import { secondsToDuration } from "../utils/duration/index";

// displays the total (not leftover) set duration of the current session
function CurrentSessionTotalDuration({currentSession, focusDuration, breakDuration}) {
    // if(currentSession === null) {return ''}
    // console.log(currentSession?.label)
    return currentSession?.label === 'Focusing' ? secondsToDuration(focusDuration) : secondsToDuration(breakDuration)
    //   return minutesToDuration(focusDuration)
    // } else {
    //   return minutesToDuration(breakDuration)
    // }
  }

export default CurrentSessionTotalDuration;
