function DurationSetter(event, time) {
    const focusOrBreak = event.target.name;
    const decreaseButton = event.target.value;
    if(focusOrBreak === 'focus') time = Math.min(Math.max(time, 15), 60)
    if(focusOrBreak === 'break') time = Math.min(Math.max(time, 4), 7)
    return decreaseButton === 'decrease' ? time -= 1 : time += 1;
}

export default DurationSetter;
